BEGIN
  FOR r_product IN (
        SELECT 
            LUNES+MARTES+MIERCOLES+JUEVES+VIERNES AS TOT, CLAVE
        FROM 
            ALI_FANEXOD
        ORDER BY CLAVE ASC
    )
  LOOP
     dbms_output.put_line( r_product.CLAVE ||
        ': $' || 
        r_product.TOT );
UPDATE ATER.ALI_FANEXOD
 SET  ATER.ALI_FANEXOD.TOTAL= (r_product.TOT)
WHERE (ATER.ALI_FANEXOD.CLAVE IN (r_product.CLAVE));
COMMIT;
  END LOOP;
END;

