BEGIN
  IF ('#SIG.vStgCargaStatus' = 'PROCESADO') THEN
    UPDATE stg.ban_config_stg
    SET cos_last_date_sucess = TO_DATE('#SIG.vStgFechaCargaBan', '#SIG.vStgFormatoFecha')
    WHERE cos_source = '#SIG.vStgNombreTablaStg'
      --AND cos_status_carga = 'POR PROCESAR'
      AND cos_last_date_sucess < TO_DATE('#SIG.vStgFechaCarga', '#SIG.vStgFormatoFecha');
  END IF;
END;

