<%

/* Validate COMPATIBLE */
if (!(
	"7".equals( odiRef.getOption ("COMPATIBLE") )  ||
	"8".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"9".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"10".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"11".equals( odiRef.getOption ("COMPATIBLE") )
))  {
	%>raise """\n'<%=odiRef.getOption( "COMPATIBLE" )%>' is not a valid value for COMPATIBLE. Please choose valid value."""
<%} else {
	%># => OK, '<%=odiRef.getOption( "COMPATIBLE" )%>' is a valid value for COMPATIBLE.
<% }; 
%>

queryGetVersion = """ /* This query returns the COMPATIBLE setting of the RDBMS */ select case when upper(name) = 'COMPATIBLE' then to_number(substr(value, 1, instr(value, '.',1,1)-1)) else null end v0, case when upper(name) = 'COMPATIBLE' then to_number(substr(value, instr(value, '.',1,1)+1, instr(value, '.',1,2)-instr(value, '.',1,1)-1)) else null end v1 from v$parameter where upper(name) = 'COMPATIBLE' """
# Validate source database version con = odiRef.getJDBCConnection("SRC") stmt = con.createStatement() rs = stmt.executeQuery( queryGetVersion )
if (rs.next()): if (rs.getInt("V0") < <%=odiRef.getOption( "COMPATIBLE" )%>): raise '\nYou are using RDBMS version %s.%s as source/staging. This version does not support IKM COMPATIBLE= <%=odiRef.getOption( "COMPATIBLE" )%>.' % ( rs.getInt("V0"), rs.getInt("V1") )
# Validate target database version con = odiRef.getJDBCConnection("DEST") stmt = con.createStatement() rs = stmt.executeQuery( queryGetVersion )
if (rs.next()): if (rs.getInt("V0") < <%=odiRef.getOption( "COMPATIBLE" )%>): raise '\nYou are using RDBMS version %s.%s as target. This version does not support IKM COMPATIBLE= <%=odiRef.getOption( "COMPATIBLE" )%>.' % ( rs.getInt("V0"), rs.getInt("V1") )

DECLARE
Q VARCHAR2(2000);
DBLINK VARCHAR (200);
BEGIN
SELECT NGV_VALUE INTO DBLINK
FROM BAN_MASTER 
WHERE NGV_NAME = 'DBLINK_STG_TO_OFSAA';

SELECT 'DELETE FROM ' || DPO_TABLE_OFSAA || '@'||DBLINK||' WHERE '
 || DPO_COLUMN_NAME || ' ' || DPO_VALUE || ' AND FIC_MIS_DATE = TO_DATE(''#vStgFechaParticionOfsaa'',''#vStgFormatoFecha'')' INTO Q 
FROM BAN_DELETE_PART_OFSAA 
WHERE DPO_TABLE_STG = '#vStgNombreTablaStg';

EXECUTE IMMEDIATE  Q;

EXCEPTION
WHEN NO_DATA_FOUND THEN
NULL;

END;

DECLARE  -- Validating KM options
<% if (odiRef.getOption("ENABLE_EDITION_SUPPORT").equals("1")) { 
%>	editionsEnabled CHAR(1);	/* For Edition support */
<%};%>	dbVersion1	NUMBER(2,0);
	dbVersion2	NUMBER(2,0);
BEGIN
<%
if (!(
	"9".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"10".equals( odiRef.getOption ("COMPATIBLE") ) ||
	"11".equals( odiRef.getOption ("COMPATIBLE") )
))  { %>
	-- Validate COMPATIBLE option
	raise_application_error(-20101, 'Invalid value for CKM option COMPATIBLE');
<% };

%>

	-- Verify RDBMS version
	select
		case when upper(name) = 'COMPATIBLE' then to_number(substr(value, 1, instr(value, '.',1,1)-1)) else null end v0,
		case when upper(name) = 'COMPATIBLE' then to_number(substr(value, instr(value, '.',1,1)+1, instr(value, '.',1,2)-instr(value, '.',1,1)-1)) else null end v1
	into	dbVersion1, dbVersion2
	from	v$parameter
	where	upper(name) =  'COMPATIBLE';

	IF dbVersion1 < <%=odiRef.getOption( "COMPATIBLE" )%> THEN
		raise_application_error(-20101, 'You are using RDBMS version '||dbVersion1||'.'||dbVersion2||'. This version does not support CKM COMPATIBLE= <%=odiRef.getOption( "COMPATIBLE" )%>.');
	END IF;

<% if (odiRef.getOption("ENABLE_EDITION_SUPPORT").equals("1")) { 

/* For Edition support */
%>
	-- Make sure editions are enabled for <%=odiRef.getInfo("DEST_SCHEMA")%>
	select	EDITIONS_ENABLED
	into	editionsEnabled
	from	DBA_USERS
	where	USERNAME	= '<%=odiRef.getInfo("DEST_SCHEMA")%>';

	IF editionsEnabled = 'N' THEN
		raise_application_error(-20101, 'Editions are not enabled for user <%=odiRef.getInfo("DEST_SCHEMA")%>. This is required for edition support. Please use ALTER USER <%=odiRef.getInfo("DEST_SCHEMA")%> ENABLE EDITIONS.');
	END IF;


	-- Make sure editions are enabled for <%=odiRef.getInfo("DEST_WORK_SCHEMA")%>
	select	EDITIONS_ENABLED
	into	editionsEnabled
	from	DBA_USERS
	where	USERNAME	= '<%=odiRef.getInfo("DEST_WORK_SCHEMA")%>';

	IF editionsEnabled = 'N' THEN
		raise_application_error(-20101, 'Editions are not enabled for user <%=odiRef.getInfo("DEST_WORK_SCHEMA")%>. This is required for edition support. Please use ALTER USER <%=odiRef.getInfo("DEST_WORK_SCHEMA")%> ENABLE EDITIONS.');
	END IF;


	-- Make sure editions are enabled for <%=odiRef.getInfo("DEST_DEFW_SCHEMA")%>
	select	EDITIONS_ENABLED
	into	editionsEnabled
	from	DBA_USERS
	where	USERNAME	= '<%=odiRef.getInfo("DEST_DEFW_SCHEMA")%>';

	IF editionsEnabled = 'N' THEN
		raise_application_error(-20101, 'Editions are not enabled for user <%=odiRef.getInfo("DEST_DEFW_SCHEMA")%>. This is required for edition support. Please use ALTER USER <%=odiRef.getInfo("DEST_DEFW_SCHEMA")%> ENABLE EDITIONS.');
	END IF;
<% }; %>

END;

